import React, { Component } from "react";
import PropTypes from "prop-types";
import { Text, StyleSheet, View, TextInput, Image, Button } from "react-native";
import Slider from "@react-native-community/slider";
import { Picker } from "@react-native-picker/picker";
import { PanResponder } from "react-native";
import styled, { css } from "styled-components/native";
import { useSafeAreaInsets } from "react-native-safe-area-context";

import {
  EDITOR_BORDER_SIZE,
  HEADER_HEIGHT,
  PIXEL_COUNT,
  PIXEL_SIZE,
  TOOLS
} from "../constants";

let array = [];
for (let i = 0; i < PIXEL_COUNT; i++) {
  for (let j = 0; j < PIXEL_COUNT; j++) {
    array.push({ color: "white" });
  }
}

const Wrapper = styled.View`
  width: 100%;
  padding: ${EDITOR_BORDER_SIZE}px;
`;

const Grid = styled.View`
  width: 100%;
  height: ${PIXEL_SIZE * PIXEL_COUNT}px;
  background: white;
  flex-direction: row;
  flex-wrap: wrap;
`;

const PixelBlock = styled.View`
  background: ${props =>
    props.color === "none" ? props.backgroundColor : props.color};
  height: ${PIXEL_SIZE}px;
  width: ${PIXEL_SIZE}px;
  ${({ displayGrid, index }) =>
    displayGrid &&
    `
    border-bottom-width: 1px;
    border-left-width: 1px;
    border-right-width: ${index % PIXEL_COUNT === 15 ? 1 : 0}px;
    border-top-width: ${index < PIXEL_COUNT ? 1 : 0}px;
  `}
`;

class CanvasGrid extends Component {
  constructor(props) {
    super(props);
    this.state = {
      backgroundColor: "white",
      currentColor: "pink",
      selectedTool: "pencil",
      displayGrid: true,
      ids: array,
      color: "pink",
      data: array
    };
  }
  static defaultProps = {};

  updateCanvas = (evt, insets) => {
    if (evt.nativeEvent.pageX > EDITOR_BORDER_SIZE + PIXEL_SIZE * PIXEL_COUNT) {
      return;
    }
    const tx = evt.nativeEvent.pageX - EDITOR_BORDER_SIZE;
    const ty =
      evt.nativeEvent.pageY - EDITOR_BORDER_SIZE - HEADER_HEIGHT - insets.top;
    const px = Math.trunc(tx / PIXEL_SIZE);
    const py = Math.trunc(ty / PIXEL_SIZE);
    const arrayPosition = py * PIXEL_COUNT + px;
    if (arrayPosition + 1 > data.length || arrayPosition < 0) {
      return;
    }
    if (
      data[arrayPosition].color ===
      (selectedTool === TOOLS.ERASER ? "none" : currentColor)
    ) {
      return;
    }
    let newData = data;
    if (selectedTool === TOOLS.BUCKET) {
      // newData = dropBucket(
      //   newData,
      //   arrayPosition,
      //   currentColor,
      //   data[arrayPosition].color,
      //   newData
      // );
    } else {
      newData[arrayPosition] = {
        color: selectedTool === TOOLS.PENCIL ? currentColor : "none"
      };
    }
    this.setState({
      data: newData
    });
  };

  panResponder = PanResponder.create({
    onStartShouldSetPanResponder: () => true,
    onStartShouldSetPanResponderCapture: () => true,
    onMoveShouldSetPanResponder: () => true,
    onMoveShouldSetPanResponderCapture: () => true,
    onPanResponderGrant: evt => this.updateCanvas(evt),
    onPanResponderMove: evt => {
      this.updateCanvas(evt);
    },
    onPanResponderTerminationRequest: () => true,
    onShouldBlockNativeResponder: () => true
  });

  render() {
    const { route, navigation } = this.props;
    const { insets } = this.props;
    return (
      <Wrapper backgroundColor={this.props.backgroundColor}>
        <Grid
          onStartShouldSetResponder={() => true}
          onStartShouldSetResponderCapture={() => true}
          onMoveShouldSetResponder={() => true}
          onMoveShouldSetResponderCapture={() => true}
          onResponderGrant={evt => this.updateCanvas(evt, insets)}
          onResponderMove={evt => {
            this.updateCanvas(evt, insets);
          }}
          onResponderTerminationRequest={() => true}
          onShouldBlockNativeResponder={() => true}
        >
          {this.state.data.map((pixel, index) => (
            <PixelBlock
              key={index}
              index={index}
              backgroundColor={this.state.backgroundColor}
              displayGrid={this.state.displayGrid}
              color={pixel.color}
              style={{ borderColor: "#E5E5E5" }}
            />
          ))}
        </Grid>
      </Wrapper>
    );
  }
}

export default function(props) {
  const insets = useSafeAreaInsets();

  return <CanvasGrid {...props} insets={insets} />;
}

const styles = StyleSheet.create({
  grid: {
    height: 260,
    width: 260,
    borderColor: "gray",
    borderWidth: 1,
    flex: 1,
    flexDirection: "row",
    flexWrap: "wrap"
  },
  cell: {
    backgroundColor: "grey",
    height: 40,
    width: 40,
    margin: 5
  },
  cellColored: {
    backgroundColor: "coral",
    height: 40,
    width: 40,
    margin: 5
  }
});
