import React, { Component } from "react";
import PropTypes from "prop-types";
import { Text, StyleSheet, View, TextInput, Image, Button } from "react-native";
import Slider from "@react-native-community/slider";
import { Picker } from "@react-native-picker/picker";
import { panResponder } from "react-native";
import styled from "styled-components/native";
import {
  EDITOR_BORDER_SIZE,
  HEADER_HEIGHT,
  PIXEL_COUNT,
  PIXEL_SIZE,
  Tools
} from "../constants";

const Wrapper =
  styled.View <
  { backgroundColor: "white" } >
  `
  background: ${({ backgroundColor }) => backgroundColor};
  width: 100%;
  padding: ${EDITOR_BORDER_SIZE}px;
`;

const Grid = styled.View`
  width: 100%;
  height: ${PIXEL_SIZE * PIXEL_COUNT}px;
  background: white;
  flex-direction: row;
  flex-wrap: wrap;
`;

const PixelBlock = styled.View`
  background: ${({ color, backgroundColor }) =>
    color === "none" ? backgroundColor : color};
  height: ${PIXEL_SIZE}px;
  width: ${PIXEL_SIZE}px;
  ${({ displayGrid, index }) =>
    displayGrid &&
    `
    border-bottom-width: 1px;
    border-left-width: 1px;
    border-right-width: ${index % PIXEL_COUNT === 15 ? 1 : 0}px;
    border-top-width: ${index < PIXEL_COUNT ? 1 : 0}px;
  `}
`;

let array = [];
for (let i = 0; i < 25; i++) {
  array.push(0);
}

export default class extends Component {
  constructor(props) {
    super(props);
    this.state = {
      backgroundColor: string,
      currentColor: string,
      selectedTool: TOOLS,
      displayGrid: boolean,
      ids: array,
      color: "pink"
    };
  }
  static defaultProps = {};

  Canvas = ({
    backgroundColor,
    data,
    updateData,
    currentColor,
    displayGrid,
    selectedTool
  }) => {
    const insets = useSafeAreaInsets();
    const updateCanvas = evt => {
      if (
        evt.nativeEvent.pageX >
        EDITOR_BORDER_SIZE + PIXEL_SIZE * PIXEL_COUNT
      ) {
        return;
      }
      const tx = evt.nativeEvent.pageX - EDITOR_BORDER_SIZE;
      const ty =
        evt.nativeEvent.pageY - EDITOR_BORDER_SIZE - HEADER_HEIGHT - insets.top;
      const px = Math.trunc(tx / PIXEL_SIZE);
      const py = Math.trunc(ty / PIXEL_SIZE);
      const arrayPosition = py * PIXEL_COUNT + px;
      if (arrayPosition + 1 > data.length || arrayPosition < 0) {
        return;
      }
      if (
        data[arrayPosition].color ===
        (selectedTool === TOOLS.ERASER ? "none" : currentColor)
      ) {
        return;
      }
      let newData = data;
      if (selectedTool === TOOLS.BUCKET) {
        newData = dropBucket(
          newData,
          arrayPosition,
          currentColor,
          data[arrayPosition].color,
          newData
        );
      } else {
        newData[arrayPosition] = {
          color: selectedTool === TOOLS.PENCIL ? currentColor : "none"
        };
      }
      updateData([...newData]);
    };
  };

  panResponder = PanResponder.create({
    onStartShouldSetPanResponder: () => true,
    onStartShouldSetPanResponderCapture: () => true,
    onMoveShouldSetPanResponder: () => true,
    onMoveShouldSetPanResponderCapture: () => true,
    onPanResponderGrant: updateCanvas,
    onPanResponderMove: evt => {
      updateCanvas(evt);
    },
    onPanResponderTerminationRequest: () => true,
    onShouldBlockNativeResponder: () => true
  });

  onResponderGrant(id, ev) {
    let ids = this.state.ids;
    ids[id] = 1;
    this.setState({
      ids
    });
    console.log(`press start${id}`);
  }

  onResponderMove(id, ev) {
    // let ids = this.state.ids;
    // ids[id] = 1;
    // this.setState({
    //   ids
    // });
    console.log(`press move${id}`);
  }

  createGrid = () => {
    let grid = [];

    let cellColored = {
      backgroundColor: this.state.color,
      height: 40,
      width: 40,
      margin: 5
    };

    for (let j = 0; j < 25; j++) {
      grid.push(
        <View
          id={j}
          key={j}
          style={this.state.ids[j] === 0 ? styles.cell : cellColored}
          onStartShouldSetResponder={ev => true}
          onMoveShouldSetResponder={ev => true}
          onResponderGrant={ev => this.onResponderGrant(j, ev)}
          onResponderMove={ev => this.onResponderMove(j, ev)}
          onResponderRelease={ev => {}}
          onResponderTerminationRequest={ev => true}
          onResponderTer={ev => {}}
        >
          <Text>{this.state.ids[j]}</Text>
        </View>
      );
    }
    return grid;
  };

  render() {
    const { route, navigation } = this.props;
    const { newColor } = route.params == null ? "grey" : route.params;
    return (
      <Wrapper backgroundColor={backgroundColor}>
        <Grid {...panResponder.panResponder}></Grid>
        <Grid {...panResponder.panHandlers}>
          {data.map((pixel, index) => (
            <PixelBlock
              key={index}
              index={index}
              backgroundColor={backgroundColor}
              displayGrid={displayGrid}
              color={pixel.color}
              style={{ borderColor: "#E5E5E5" }}
            />
          ))}
        </Grid>
      </Wrapper>
    );
  }
}

const styles = StyleSheet.create({
  grid: {
    height: 260,
    width: 260,
    borderColor: "gray",
    borderWidth: 1,
    flex: 1,
    flexDirection: "row",
    flexWrap: "wrap"
  },
  cell: {
    backgroundColor: "grey",
    height: 40,
    width: 40,
    margin: 5
  },
  cellColored: {
    backgroundColor: "coral",
    height: 40,
    width: 40,
    margin: 5
  }
});
