import React, { Component } from "react";
import PropTypes from "prop-types";
import { Text, StyleSheet, View, TextInput, Image, Button } from "react-native";
import Slider from "@react-native-community/slider";
import { Picker } from "@react-native-picker/picker";
import Grid from "./CanvasGrid";

export default class Canvas extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  static defaultProps = {};

  render() {
    const { route, navigation } = this.props;
    const { newColor } = route.params == null ? "white" : route.params;
    return (
      <View style={styles.grid}>
        <Button
          title="Pick Color"
          onPress={() => {
            this.props.navigation.navigate("ColorPicker");
          }}
        />
        <Button
          title="Set Color"
          onPress={() => {
            this.setState({ color: newColor });
          }}
        />

        <Text>color:{JSON.stringify(newColor)}</Text>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  grid: {
    height: 260,
    width: 260,
    borderColor: "gray",
    borderWidth: 1,
    flex: 1,
    flexDirection: "row",
    flexWrap: "wrap"
  },
  cell: {
    backgroundColor: "grey",
    height: 40,
    width: 40,
    margin: 5
  },
  cellColored: {
    backgroundColor: "coral",
    height: 40,
    width: 40,
    margin: 5
  }
});
